import React from "react";
import "./OverviewSection.scss";
// import { RootState } from "../../../types/types";
import { makeStyles, Theme } from "@mui/styles";
import { RootState } from "../../../types/interfaces";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import Divider from "@mui/material/Divider";
import ListItemIcon from "@mui/material/ListItemIcon";
import ListItemText from "@mui/material/ListItemText";
import AssignmentIcon from "@mui/icons-material/Assignment";
import ShoppingCartIcon from "@mui/icons-material/ShoppingCart";
import DoneIcon from "@mui/icons-material/Done";
import PendingIcon from "@mui/icons-material/Pending";
import { Typography } from "@mui/material";
import { useSelector } from "react-redux";

const useStyles = makeStyles((theme: Theme) => ({
  highlightedItem: {
    backgroundColor: theme.palette.action.hover, // Измените цвет фона по вашему усмотрению
    borderRadius: theme.shape.borderRadius,
    marginBottom: theme.spacing(1),
  },
}));

// type Props = { orders: Order[] };

// function OverviewSection({ orders }: Props) {
function OverviewSection() {
  const classes = useStyles();
  const orders = useSelector((state: RootState) => state.orders);

  const totalOrders = orders.length;
  const totalRevenue = orders.reduce(
    (acc, curr) => acc + curr.requiredQuantity,
    0
  );

  const newOrders = orders.filter((order) => order.orderStatus === "NEW");
  const confirmedOrders = orders.filter(
    (order) => order.orderStatus === "CONFIRMED"
  ).length;
  const canceledOrders = orders.filter(
    (order) => order.orderStatus === "CANCELED"
  ).length;
  const finishedOrders = orders.filter(
    (order) => order.orderStatus === "DONE"
  ).length;

  return (
    <div>
      <Typography variant='h3'>Overview</Typography>
      {/* <h2 className='overview-section__title'>Overview</h2> */}
      <List>
        <ListItem className={classes.highlightedItem}>
          <ListItemIcon>
            <AssignmentIcon />
          </ListItemIcon>
          <ListItemText
            primary='Total Orders'
            secondary={`${totalOrders} pcs`}
          />
        </ListItem>
        <ListItem className={classes.highlightedItem}>
          <ListItemIcon>
            <ShoppingCartIcon />
          </ListItemIcon>
          <ListItemText
            primary='Ordered Quantity'
            secondary={`${totalRevenue} pcs`}
          />
        </ListItem>

        <Divider />

        <ListItem>
          <ListItemIcon>
            <PendingIcon />
          </ListItemIcon>
          <ListItemText
            primary='New Orders'
            secondary={`${newOrders.length} pcs`}
          />
        </ListItem>

        <ListItem>
          <ListItemIcon>
            <PendingIcon />
          </ListItemIcon>
          <ListItemText
            primary='Confirmed Orders'
            secondary={`${confirmedOrders} pcs`}
          />
        </ListItem>

        <ListItem>
          <ListItemIcon>
            <PendingIcon />
          </ListItemIcon>
          <ListItemText
            primary='Canceled Orders'
            secondary={`${canceledOrders} pcs`}
          />
        </ListItem>

        <ListItem>
          <ListItemIcon>
            <DoneIcon />
          </ListItemIcon>
          <ListItemText
            primary='Finished Orders'
            secondary={`${finishedOrders} pcs`}
          />
        </ListItem>
      </List>
    </div>
  );
}

export default OverviewSection;
