import React from "react";
import "./OverviewSection.scss";
import { Order } from "../../../types/types";

type Props = { orders: Order[] };

function OverviewSection({ orders }: Props) {
  console.log(orders);

  const totalOrders = orders.length;
  const totalRevenue = orders.reduce(
    (acc, curr) => acc + curr.requiredQuantity,
    0
  );
  const openOrders = orders.filter(
    (order) => order.orderStatus === "OPEN"
  ).length;

  const finishedOrders = orders.filter(
    (order) => order.orderStatus === "DONE"
  ).length;

  return (
    <div>
      <h2 className='overview-section__title'>Overview</h2>
      <p className='overview-section__text'> Orders total: {totalOrders} pcs</p>
      <p className='overview-section__text'>
        {" "}
        Ordered quantity: {totalRevenue} pcs
      </p>
      <p className='overview-section__text'> Open orders: {openOrders} pcs</p>
    </div>
  );
}

export default OverviewSection;
