import React from "react";
import "./OverviewSection.scss";
import { Order } from "../../../types/types";
import {
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
} from "@mui/material";

type Props = { orders: Order[] };

function OverviewSection({ orders }: Props) {
  const totalOrders = orders.length;
  const totalRevenue = orders.reduce(
    (acc, curr) => acc + curr.requiredQuantity,
    0
  );
  const openOrders = orders.filter(
    (order) => order.orderStatus === "OPEN"
  ).length;
  const finishedOrders = orders.filter(
    (order) => order.orderStatus === "DONE"
  ).length;

  return (
    <div>
      <h2 className='overview-section__title'>Overview</h2>
      <TableContainer component={Paper}>
        <Table aria-label='simple table'>
          <TableHead>
            <TableRow>
              <TableCell>Info</TableCell>
              <TableCell align='right'>Quantity, pcs</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            <TableRow>
              <TableCell component='th' scope='row'>
                Total
              </TableCell>
              <TableCell align='right'>{totalOrders}</TableCell>
            </TableRow>
            <TableRow>
              <TableCell component='th' scope='row'>
                Ordered Quantity
              </TableCell>
              <TableCell align='right'>{totalRevenue}</TableCell>
            </TableRow>
            <TableRow>
              <TableCell component='th' scope='row'>
                Open Orders
              </TableCell>
              <TableCell align='right'>{openOrders}</TableCell>
            </TableRow>
            <TableRow>
              <TableCell component='th' scope='row'>
                Finished Orders
              </TableCell>
              <TableCell align='right'>{finishedOrders}</TableCell>
            </TableRow>
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
}

export default OverviewSection;
